import * as THREE from "three";
// 导入轨道控制器
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls.js";

// 目标 纹理的属性
// 创建场景
const scene = new THREE.Scene();

// 创建透视相机
const camera = new THREE.PerspectiveCamera(
  75,
  window.innerWidth / window.innerHeight,
  0.1,
  1000
);
// 设置相机位置
camera.position.set(0, 0, 10);
scene.add(camera);

// 导入纹理
const textureLoader = new THREE.TextureLoader();
const mutouColorTexture = textureLoader.load("./img/mutou.jpg");
console.log(mutouColorTexture);
// 纹理的偏移
// mutouColorTexture.offset.x = 0.5;
// mutouColorTexture.offset.y = 0.5;
mutouColorTexture.offset.set(0.5, 0.5);
// 纹理的旋转
mutouColorTexture.center.set(0.5, 0.5);
mutouColorTexture.rotation = Math.PI / 4;
// 纹理的重复
mutouColorTexture.repeat.set(2, 3);
// 设置纹理重复的模式
// 水平
mutouColorTexture.wrapS = THREE.RepeatWrapping;
// 垂直
mutouColorTexture.wrapT = THREE.MirroredRepeatWrapping;

// 添加物体
// 创建几何体a
const cubeGeometry = new THREE.BoxBufferGeometry(1, 1, 1);
// 材质
const basicMaterial = new THREE.MeshBasicMaterial({
  color: "#ffff00",
  map: mutouColorTexture,
});

const cube = new THREE.Mesh(cubeGeometry, basicMaterial);
scene.add(cube);

// 初始化渲染器
const renderer = new THREE.WebGLRenderer();
// 设置渲染的尺寸大小
renderer.setSize(window.innerWidth, window.innerHeight);
// 将webgl渲染的canvas内容添加到body
document.body.appendChild(renderer.domElement);

//使用渲染器通过相机将场景渲染出来
// renderer.render(scene, camera);

// 创建轨道控制器
const controls = new OrbitControls(camera, renderer.domElement);
// 设置控制器阻尼 让控制器更有真实效果
controls.enableDamping = true;
// 添加坐标轴辅助器
const axesHelper = new THREE.AxesHelper(5);
scene.add(axesHelper);
// 设置动画
// var animate1 = gsap.to(cube.position, {
//   x: 5,
//   duration: 5,
//   ease: "power1.inOut",
//   onComplete: () => {
//     console.log("动画完成");
//   },
//   onStart: () => {
//     console.log("动画开始");
//   },
//   // 重复次数 无限次循环-1
//   repeat: -1,
//   // 往返运动
//   yoyo: true,
//   delay: 2,
// });
// gsap.to(cube.rotation, { x: 2 * Math.PI, duration: 5, ease: "power1.inOut" });
function render(time) {
  controls.update();
  renderer.render(scene, camera);
  // 渲染下一帧的时候就会调用render函数
  requestAnimationFrame(render);
}

render();
